	public void update(int delta) throws SlickException {
		if (spawns.size() > 0 && spawns.size() > spawncap) {
			MainGame.getEntities().remove(spawns.get(0));
			spawns.remove(0);
		}
		if (health > maxhealth) {
			health = maxhealth;
		}
		if (health <= 0) {
			onDeath();
			destroy = true;
		}
		if (o != null && linger == false) {
			if (o.destroy == true)
				this.destroy = true;
		}
		if (active == true) {
			if (size != lSize) {
				image = image.getScaledCopy((int)(image.getWidth()*size), (int)(image.getHeight()*size));
				init();
				lSize = size;
			} 
			if (wOverride > 0  && hitbox.getWidth() != wOverride)
				hitbox.setWidth(wOverride);
			if (hOverride > 0 && hitbox.getHeight() != hOverride)
				hitbox.setHeight(hOverride);
			if (!overrideMove && controller != null)
				controller.move(delta);
			collisionbox.setCenterX(getLocation().getX());
			collisionbox.setY(getLocation().getY()-collisionbox.getHeight()+2);
			hitbox.setCenterX(getLocation().getX());
			hitbox.setY(getLocation().getY()-hitbox.getHeight()+2);
			influence.setCenterX(getLocation().getX());
			influence.setCenterY(getLocation().getY());
			for (int i = spawns.size()-1; i > -1; i--) {
				Entity e = spawns.get(i);
				if (e.destroy == true) {
					spawns.remove(e);
					continue;
				}
				if (!MainGame.getEntities().contains(e) && e.destroy != true)
					MainGame.getEntities().add(e);
				if (e.team != this.team)
					e.team = this.team;
			}
		} else {
			return;
		}
	}
	
	public void onDeath() throws SlickException {
		if (natural && MainGame.mm.natspawns > 0) {
			MainGame.mm.natspawns--;
		}
		for (Entity s : spawns) {
			s.destroy = true;
		}
		spawns.clear();
		destroy = true;
	}
